(function(){
	$(".collapsed").on("click", function(){
		$(".panel-heading").removeClass("bg-grey").find("h4").removeClass("ft-white");
		$(".panel-heading").find(".sprite").removeClass("icon-top").addClass("icon-bottom");
		var $selector = $(this);
		if($selector.attr("aria-expanded") == "true"){ 
			$selector.find(".sprite").removeClass("icon-top").addClass("icon-bottom");
			$selector.find(".panel-heading").removeClass("bg-grey").find("h4").removeClass("ft-white");
		}
		else {
			$selector.find(".sprite").removeClass("icon-bottom").addClass("icon-top");
			$selector.find(".panel-heading").addClass("bg-grey").find("h4").addClass("ft-white");	
		}
	});
}());
(function($) {
    "use strict"; // Start of use strict

    // jQuery for page scrolling feature - requires jQuery Easing plugin
    $('a.page-scroll').bind('click', function(event) {
        var $anchor = $(this);
        $('html, body').stop().animate({
            scrollTop: ($($anchor.attr('href')).offset().top - 50)
        }, 1250, 'easeInOutExpo');
        event.preventDefault();
    });
    
    // Highlight the top nav as scrolling occurs
    $('body').scrollspy({
        target: '.navbar-fixed-top',
        offset: 51
    });

    // Closes the Responsive Menu on Menu Item Click
    $('.navbar-collapse ul li a').click(function() {
        $('.navbar-toggle:visible').click();
    });

    // Offset for Main Navigation
    /*$('#mainNav').affix({
        offset: {
            top: 100
        }
    })*/

    // Initialize and Configure Scroll Reveal Animation
    window.sr = ScrollReveal();
    sr.reveal('.sr-icons', {
        duration: 600,
        scale: 0.3,
        distance: '0px'
    }, 200);
    sr.reveal('.sr-button', {
        duration: 1000,
        delay: 200
    });
    sr.reveal('.sr-contact', {
        duration: 600,
        scale: 0.3,
        distance: '0px'
    }, 300);

    // Initialize and Configure Magnific Popup Lightbox Plugin
    $('.popup-gallery').magnificPopup({
        delegate: 'a',
        type: 'image',
        tLoading: 'Loading image #%curr%...',
        mainClass: 'mfp-img-mobile',
        gallery: {
            enabled: true,
            navigateByImgClick: true,
            preload: [0, 1] // Will preload 0 - before current, and 1 after the current image
        },
        image: {
            tError: '<a href="%url%">The image #%curr%</a> could not be loaded.'
        }
    });

})(jQuery); // End of use strict

